=========================================================
* Material Dashboard 2 React - v2.2.0
=========================================================
*/

// @mui material components
import Grid from "@mui/material/Grid";
import Typography from "@mui/material/Typography";
import { useEffect, useRef, useState } from "react";

// Material Dashboard 2 React components
import MDBox from "components/MDBox";
import MDButton from "components/MDButton";

// Material Dashboard 2 React example components
import DashboardLayout from "examples/LayoutContainers/DashboardLayout";
import DashboardNavbar from "examples/Navbars/DashboardNavbar";
import Footer from "examples/Footer";
import Card from "@mui/material/Card";
import Box from "@mui/material/Box";

function PostureSense() {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const stageRef = useRef(null);
  const counterRef = useRef(0);
  const [displayCounter, setDisplayCounter] = useState(0);
  const [displayStage, setDisplayStage] = useState(null);
  const [isCameraActive, setIsCameraActive] = useState(false);
  const [isCameraOn, setIsCameraOn] = useState(false);
  const [leftStage, setLeftStage] = useState(null);
  const [leftCounter, setLeftCounter] = useState(0);
  const [rightStage, setRightStage] = useState(null);
  const [rightCounter, setRightCounter] = useState(0);
  const [leftAngle, setLeftAngle] = useState(0);
  const [rightAngle, setRightAngle] = useState(0);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    let video = null;
    let pose = null;
    let animationFrame = null;

    const startCamera = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ video: true });
        video = videoRef.current;
        video.srcObject = stream;

        // Wait for video to be ready
        await new Promise((resolve) => {
          video.onloadedmetadata = () => {
            video.play();
            resolve();
          };
        });

        // Initialize MediaPipe Pose
        const mp = await import("@mediapipe/pose");
        const mp_drawing = await import("@mediapipe/drawing_utils");
        pose = new mp.Pose({
          locateFile: (file) => {
            return `https://cdn.jsdelivr.net/npm/@mediapipe/pose/${file}`;
          },
        });

        pose.setOptions({
          modelComplexity: 1,
          smoothLandmarks: true,
          minDetectionConfidence: 0.5,
          minTrackingConfidence: 0.5,
        });

        pose.onResults((results) => {
          if (results.poseLandmarks) {
            const landmarks = results.poseLandmarks;
            const canvas = canvasRef.current;
            const ctx = canvas.getContext("2d");
            // Set canvas size to match video
            canvas.width = video.videoWidth;
            canvas.height = video.videoHeight;

            // Clear the canvas
            ctx.clearRect(0, 0, canvas.width, canvas.height);

            // Save the current context state
            ctx.save();
            
            // Scale the context horizontally to flip the image
            ctx.scale(-1, 1);
            ctx.translate(-canvas.width, 0);
            
            // Draw video frame
            ctx.drawImage(video, 0, 0, canvas.width, canvas.height);

            // Get coordinates
            const shoulder = [
              landmarks[11].x, // LEFT_SHOULDER
              landmarks[11].y,
            ];
            const elbow = [
              landmarks[13].x, // LEFT_ELBOW
              landmarks[13].y,
            ];
            const wrist = [
              landmarks[15].x, // LEFT_WRIST
              landmarks[15].y,
            ];

            // Calculate angle
            let angle = Math.atan2(wrist[1] - elbow[1], wrist[0] - elbow[0]) -
              Math.atan2(shoulder[1] - elbow[1], shoulder[0] - elbow[0]);
            angle = Math.abs((angle * 180.0) / Math.PI);

            if (angle > 180.0) {
              angle = 360 - angle;
            }

            // Curl counter logic
            if (angle > 160) {
              stageRef.current = "DOWN";
              setDisplayStage("DOWN");
            }
            if (angle < 30 && stageRef.current === "DOWN") {
              stageRef.current = "UP";
              setDisplayStage("UP");
              counterRef.current += 1;
              setDisplayCounter(counterRef.current);
            }

            // Draw landmarks
            mp_drawing.drawConnectors(ctx, results.poseLandmarks, mp.POSE_CONNECTIONS, {
              color: "#F57542",
              lineWidth: 2,
              radius: 2,
            });
            mp_drawing.drawLandmarks(ctx, results.poseLandmarks, {
              color: "#F542E6",
              lineWidth: 2,
              radius: 2,
            });

            // Restore the context state
            ctx.restore();

            // Draw angle (outside inverted context)
            ctx.fillStyle = "white";
            ctx.font = "20px Arial";
            ctx.fillText(
              `${Math.round(angle)}°`,
              (1 - elbow[0]) * canvas.width,
              elbow[1] * canvas.height
            );

            // Draw counter and stage (normal orientation)
            ctx.fillStyle = "rgba(100, 100, 100, 0.8)";
            ctx.fillRect(0, 0, 200, 70);
            ctx.fillStyle = "black";
            ctx.font = "16px Arial";
            ctx.fillText("REPS", 20, 20);
            ctx.fillStyle = "white";
            ctx.font = "24px Arial";
            ctx.fillText(counterRef.current.toString(), 25, 60);
            ctx.fillStyle = "black";
            ctx.font = "16px Arial";
            ctx.fillText("STAGE", 100, 20);
            ctx.fillStyle = "white";
            ctx.font = "20px Arial";
            ctx.fillText(stageRef.current || "", 100, 60);
          }
        });

        // Start processing
        const processFrame = async () => {
          if (video && pose && video.readyState === video.HAVE_ENOUGH_DATA) {
            await pose.send({ image: video });
            animationFrame = requestAnimationFrame(processFrame);
          }
        };

        processFrame();
        setIsCameraActive(true);
      } catch (error) {
        console.error("Error accessing camera:", error);
      }
    };

    startCamera();

    return () => {
      if (animationFrame) {
        cancelAnimationFrame(animationFrame);
      }
      if (video && video.srcObject) {
        video.srcObject.getTracks().forEach((track) => track.stop());
      }
      if (pose) {
        pose.close();
      }
      setIsCameraActive(false);
    };
  }, []); // Remove stage and counter from dependencies

  const calculateAngle = (landmarks, shoulder, elbow, wrist) => {
    const shoulderPoint = [
      landmarks[shoulder].x * canvasRef.current.width,
      landmarks[shoulder].y * canvasRef.current.height,
    ];
    const elbowPoint = [
      landmarks[elbow].x * canvasRef.current.width,
      landmarks[elbow].y * canvasRef.current.height,
    ];
    const wristPoint = [
      landmarks[wrist].x * canvasRef.current.width,
      landmarks[wrist].y * canvasRef.current.height,
    ];

    const angle = Math.atan2(wristPoint[1] - elbowPoint[1], wristPoint[0] - elbowPoint[0]) -
      Math.atan2(shoulderPoint[1] - elbowPoint[1], shoulderPoint[0] - elbowPoint[0]);

    let degrees = Math.abs(angle * 180.0 / Math.PI);
    if (degrees > 180.0) degrees = 360 - degrees;
    return degrees;
  };

  const detectPose = async () => {
    if (!videoRef.current || !canvasRef.current) return;

    const video = videoRef.current;
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");

    // Set canvas size to match video
    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;

    // Draw video frame
    ctx.save();
    ctx.scale(-1, 1);
    ctx.translate(-canvas.width, 0);
    ctx.drawImage(video, 0, 0, canvas.width, canvas.height);
    ctx.restore();

    // Detect pose
    const results = await pose.detectForVideo(video, Date.now());
    if (results.poseLandmarks) {
      const landmarks = results.poseLandmarks;

      // Calculate angles for both arms
      const leftArmAngle = calculateAngle(landmarks, 11, 13, 15);
      const rightArmAngle = calculateAngle(landmarks, 12, 14, 16);

      // Update angles
      setLeftAngle(leftArmAngle);
      setRightAngle(rightArmAngle);

      // Draw landmarks and angles
      ctx.save();
      ctx.scale(-1, 1);
      ctx.translate(-canvas.width, 0);

      // Draw left arm landmarks and angle
      ctx.beginPath();
      ctx.moveTo(landmarks[11].x * canvas.width, landmarks[11].y * canvas.height);
      ctx.lineTo(landmarks[13].x * canvas.width, landmarks[13].y * canvas.height);
      ctx.lineTo(landmarks[15].x * canvas.width, landmarks[15].y * canvas.height);
      ctx.strokeStyle = "#00ff00";
      ctx.lineWidth = 2;
      ctx.stroke();

      // Draw right arm landmarks and angle
      ctx.beginPath();
      ctx.moveTo(landmarks[12].x * canvas.width, landmarks[12].y * canvas.height);
      ctx.lineTo(landmarks[14].x * canvas.width, landmarks[14].y * canvas.height);
      ctx.lineTo(landmarks[16].x * canvas.width, landmarks[16].y * canvas.height);
      ctx.strokeStyle = "#ff0000";
      ctx.lineWidth = 2;
      ctx.stroke();

      // Draw angles
      ctx.font = "16px Arial";
      ctx.fillStyle = "#00ff00";
      ctx.fillText(`Left: ${Math.round(leftArmAngle)}°`, 20, 30);
      ctx.fillStyle = "#ff0000";
      ctx.fillText(`Right: ${Math.round(rightArmAngle)}°`, canvas.width - 100, 30);

      ctx.restore();

      // Update curl counter for left arm
      if (leftArmAngle > 160) {
        setLeftStage("down");
      }
      if (leftArmAngle < 30 && leftStage === "down") {
        setLeftStage("up");
        setLeftCounter((prev) => prev + 1);
      }

      // Update curl counter for right arm
      if (rightArmAngle > 160) {
        setRightStage("down");
      }
      if (rightArmAngle < 30 && rightStage === "down") {
        setRightStage("up");
        setRightCounter((prev) => prev + 1);
      }
    }

    requestAnimationFrame(detectPose);
  };

  return (
    <DashboardLayout>
      <DashboardNavbar />
      <MDBox py={3}>
        <Grid container spacing={3}>
          <Grid item xs={12}>
            <MDBox mb={3}>
              <Typography variant="h4" fontWeight="bold">
                PostureSense
              </Typography>
              <Typography variant="body1" color="text.secondary">
                Real-time posture detection and exercise tracking
              </Typography>
            </MDBox>
          </Grid>
          <Grid item xs={12}>
            <Card>
              <MDBox p={3}>
                <Grid container spacing={3}>
                  <Grid item xs={12} md={8}>
                    <Box
                      sx={{
                        position: "relative",
                        width: "100%",
                        height: "500px",
                        backgroundColor: "background.paper",
                        borderRadius: "12px",
                        overflow: "hidden",
                        border: "1px solid",
                        borderColor: "divider",
                      }}
                    >
                      <video
                        ref={videoRef}
                        style={{
                          width: "100%",
                          height: "100%",
                          objectFit: "cover",
                          transform: "scaleX(-1)",
                        }}
                        playsInline
                      />
                      <canvas
                        ref={canvasRef}
                        style={{
                          position: "absolute",
                          top: 0,
                          left: 0,
                          width: "100%",
                          height: "100%",
                        }}
                      />
                      <Box
                        sx={{
                          position: "absolute",
                          top: 20,
                          left: 20,
                          backgroundColor: "rgba(0,0,0,0.7)",
                          color: "white",
                          padding: "10px 20px",
                          borderRadius: "8px",
                          zIndex: 1,
                        }}
                      >
                        <Typography variant="h6">Left Arm</Typography>
                        <Typography>Stage: {leftStage || "None"}</Typography>
                        <Typography>Count: {leftCounter}</Typography>
                        <Typography>Angle: {Math.round(leftAngle)}°</Typography>
                      </Box>
                      <Box
                        sx={{
                          position: "absolute",
                          top: 20,
                          right: 20,
                          backgroundColor: "rgba(0,0,0,0.7)",
                          color: "white",
                          padding: "10px 20px",
                          borderRadius: "8px",
                          zIndex: 1,
                        }}
                      >
                        <Typography variant="h6">Right Arm</Typography>
                        <Typography>Stage: {rightStage || "None"}</Typography>
                        <Typography>Count: {rightCounter}</Typography>
                        <Typography>Angle: {Math.round(rightAngle)}°</Typography>
                      </Box>
                    </Box>
                  </Grid>
                  <Grid item xs={12} md={4}>
                    <Box
                      sx={{
                        height: "100%",
                        display: "flex",
                        flexDirection: "column",
                        gap: 2,
                      }}
                    >
                      <Card>
                        <MDBox p={3}>
                          <Typography variant="h6" gutterBottom>
                            Instructions
                          </Typography>
                          <Typography variant="body2" color="text.secondary">
                            1. Position yourself in front of the camera
                          </Typography>
                          <Typography variant="body2" color="text.secondary">
                            2. Make sure your arms are visible
                          </Typography>
                          <Typography variant="body2" color="text.secondary">
                            3. Perform bicep curls with both arms
                          </Typography>
                          <Typography variant="body2" color="text.secondary">
                            4. Watch the angle and counter updates
                          </Typography>
                        </MDBox>
                      </Card>
                      <Card>
                        <MDBox p={3}>
                          <Typography variant="h6" gutterBottom>
                            Tips
                          </Typography>
                          <Typography variant="body2" color="text.secondary">
                            • Keep your back straight
                          </Typography>
                          <Typography variant="body2" color="text.secondary">
                            • Maintain proper form
                          </Typography>
                          <Typography variant="body2" color="text.secondary">
                            • Move slowly and controlled
                          </Typography>
                        </MDBox>
                      </Card>
                    </Box>
                  </Grid>
                </Grid>
              </MDBox>
            </Card>
          </Grid>
        </Grid>
      </MDBox>
      <Footer />
    </DashboardLayout>
  );
}

export default PostureSense;
