* Material Dashboard 2 React - v2.2.0
=========================================================
*/

// @mui material components
import Grid from "@mui/material/Grid";
import Typography from "@mui/material/Typography";
import { useEffect, useRef, useState } from "react";
import Select from "@mui/material/Select";
import MenuItem from "@mui/material/MenuItem";
import FormControl from "@mui/material/FormControl";
import InputLabel from "@mui/material/InputLabel";

// Material Dashboard 2 React components
import MDBox from "components/MDBox";
import MDButton from "components/MDButton";

// Material Dashboard 2 React example components
import DashboardLayout from "examples/LayoutContainers/DashboardLayout";
import DashboardNavbar from "examples/Navbars/DashboardNavbar";
import Footer from "examples/Footer";

// Exercise configurations
const exercises = {
  bicepCurls: {
    name: "Bicep Curls",
    instructions: [
      "Stand with your back straight and arms at your sides",
      "Keep your elbows close to your body",
      "Curl both arms up until your biceps are fully contracted",
      "Lower your arms back down with controlled movement"
    ],
    landmarks: {
      left: { shoulder: 11, elbow: 13, wrist: 15 },
      right: { shoulder: 12, elbow: 14, wrist: 16 }
    },
    downAngle: 160,
    upAngle: 30
  },
  pushups: {
    name: "Push-ups",
    instructions: [
      "Get into a plank position with hands shoulder-width apart",
      "Keep your body straight from head to heels",
      "Lower your body until your chest nearly touches the ground",
      "Push back up to starting position"
    ],
    landmarks: {
      left: { shoulder: 11, elbow: 13, wrist: 15 },
      right: { shoulder: 12, elbow: 14, wrist: 16 }
    },
    downAngle: 90,
    upAngle: 30,
    invertStages: false,
    isVertical: true
  },
  squats: {
    name: "Squats",
    instructions: [
      "Stand with feet shoulder-width apart",
      "Keep your back straight",
      "Lower your body until thighs are parallel to ground",
      "Push back up to starting position"
    ],
    landmarks: {
      left: { shoulder: 23, elbow: 25, wrist: 27 },
      right: { shoulder: 24, elbow: 26, wrist: 28 }
    },
    downAngle: 90,
    upAngle: 30,
    invertStages: true
  },
  shoulderPress: {
    name: "Shoulder Press",
    instructions: [
      "Stand with feet shoulder-width apart",
      "Hold weights at shoulder level",
      "Press weights overhead until arms are fully extended",
      "Lower weights back to shoulder level"
    ],
    landmarks: {
      left: { shoulder: 11, elbow: 13, wrist: 15 },
      right: { shoulder: 12, elbow: 14, wrist: 16 }
    },
    downAngle: 90,
    upAngle: 30,
    isVertical: true,
    invertStages: true
  }
};

function PostureSense() {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const stageRef = useRef(null);
  const counterRef = useRef(0);
  const [displayCounter, setDisplayCounter] = useState(0);
  const [displayStage, setDisplayStage] = useState(null);
  const [isCameraActive, setIsCameraActive] = useState(false);
  const [selectedExercise, setSelectedExercise] = useState("bicepCurls");

  useEffect(() => {
    let video = null;
    let pose = null;
    let animationFrame = null;

    const startCamera = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ video: true });
        video = videoRef.current;
        video.srcObject = stream;

        // Wait for video to be ready
        await new Promise((resolve) => {
          video.onloadedmetadata = () => {
            video.play();
            resolve();
          };
        });

        // Initialize MediaPipe Pose
        const mp = await import("@mediapipe/pose");
        const mp_drawing = await import("@mediapipe/drawing_utils");
        pose = new mp.Pose({
          locateFile: (file) => {
            return `https://cdn.jsdelivr.net/npm/@mediapipe/pose/${file}`;
          },
        });

        pose.setOptions({
          modelComplexity: 1,
          smoothLandmarks: true,
          minDetectionConfidence: 0.5,
          minTrackingConfidence: 0.5,
        });

        pose.onResults((results) => {
          if (results.poseLandmarks) {
            const landmarks = results.poseLandmarks;
            const canvas = canvasRef.current;
            const ctx = canvas.getContext("2d");
            const exercise = exercises[selectedExercise];

            // Set canvas size to match video
            canvas.width = video.videoWidth;
            canvas.height = video.videoHeight;

            // Clear the canvas
            ctx.clearRect(0, 0, canvas.width, canvas.height);

            // Save the current context state
            ctx.save();
            
            // Scale the context horizontally to flip the image
            ctx.scale(-1, 1);
            ctx.translate(-canvas.width, 0);
            
            // Draw video frame
            ctx.drawImage(video, 0, 0, canvas.width, canvas.height);

            // Get coordinates
            const shoulder = [
              landmarks[exercise.landmarks.left.shoulder].x,
              landmarks[exercise.landmarks.left.shoulder].y,
            ];
            const elbow = [
              landmarks[exercise.landmarks.left.elbow].x,
              landmarks[exercise.landmarks.left.elbow].y,
            ];
            const wrist = [
              landmarks[exercise.landmarks.left.wrist].x,
              landmarks[exercise.landmarks.left.wrist].y,
            ];

            // Calculate angle
            let angle;
            if (exercise.isVertical) {
              // For shoulder press, calculate vertical angle
              angle = Math.atan2(
                wrist[1] - shoulder[1],
                wrist[0] - shoulder[0]
              );
              angle = Math.abs((angle * 180.0) / Math.PI);
            } else {
              // For bicep curls and squats, calculate joint angle
              angle = Math.atan2(wrist[1] - elbow[1], wrist[0] - elbow[0]) -
                Math.atan2(shoulder[1] - elbow[1], shoulder[0] - elbow[0]);
              angle = Math.abs((angle * 180.0) / Math.PI);
            }

            if (angle > 180.0) {
              angle = 360 - angle;
            }

            // Curl counter logic
            if (exercise.invertStages) {
              if (angle < exercise.upAngle) {
                stageRef.current = "DOWN";
                setDisplayStage("DOWN");
              }
              if (angle > exercise.downAngle && stageRef.current === "DOWN") {
                stageRef.current = "UP";
                setDisplayStage("UP");
                counterRef.current += 1;
                setDisplayCounter(counterRef.current);
              }
            } else {
              if (angle > exercise.downAngle) {
                stageRef.current = "DOWN";
                setDisplayStage("DOWN");
              }
              if (angle < exercise.upAngle && stageRef.current === "DOWN") {
                stageRef.current = "UP";
                setDisplayStage("UP");
                counterRef.current += 1;
                setDisplayCounter(counterRef.current);
              }
            }

            // Calculate form correctness
            let isFormCorrect = false;
            if (exercise.invertStages) {
              isFormCorrect = angle >= exercise.upAngle && angle <= exercise.downAngle;
            } else {
              isFormCorrect = angle <= exercise.upAngle || angle >= exercise.downAngle;
            }

            // Draw landmarks with color based on form
            const lineColor = isFormCorrect ? "#00FF00" : "#FF0000";
            const dotColor = isFormCorrect ? "#00FF00" : "#FF0000";

            mp_drawing.drawConnectors(ctx, results.poseLandmarks, mp.POSE_CONNECTIONS, {
              color: lineColor,
              lineWidth: 2,
              radius: 2,
            });
            mp_drawing.drawLandmarks(ctx, results.poseLandmarks, {
              color: dotColor,
              lineWidth: 2,
              radius: 2,
            });

            // Restore the context state
            ctx.restore();

            // Draw angle (outside inverted context)
            ctx.fillStyle = "white";
            ctx.font = "20px Arial";
            ctx.fillText(
              `${Math.round(angle)}Â°`,
              (1 - elbow[0]) * canvas.width,
              elbow[1] * canvas.height
            );

            // Draw counter and stage (normal orientation)
            ctx.fillStyle = "rgba(100, 100, 100, 0.8)";
            ctx.fillRect(0, 0, 200, 70);
            ctx.fillStyle = "black";
            ctx.font = "16px Arial";
            ctx.fillText("REPS", 20, 20);
            ctx.fillStyle = "white";
            ctx.font = "24px Arial";
            ctx.fillText(counterRef.current.toString(), 25, 60);
            ctx.fillStyle = "black";
            ctx.font = "16px Arial";
            ctx.fillText("STAGE", 100, 20);
            ctx.fillStyle = "white";
            ctx.font = "20px Arial";
            ctx.fillText(stageRef.current || "", 100, 60);
          }
        });

        // Start processing
        const processFrame = async () => {
          if (video && pose && video.readyState === video.HAVE_ENOUGH_DATA) {
            await pose.send({ image: video });
            animationFrame = requestAnimationFrame(processFrame);
          }
        };

        processFrame();
        setIsCameraActive(true);
      } catch (error) {
        console.error("Error accessing camera:", error);
      }
    };

    startCamera();

    return () => {
      if (animationFrame) {
        cancelAnimationFrame(animationFrame);
      }
      if (video && video.srcObject) {
        video.srcObject.getTracks().forEach((track) => track.stop());
      }
      if (pose) {
        pose.close();
      }
      setIsCameraActive(false);
    };
  }, [selectedExercise]);

  return (
    <DashboardLayout>
      <DashboardNavbar />
      <MDBox py={3}>
        <Grid container spacing={3}>
          <Grid item xs={12}>
            <MDBox mb={3}>
              <Typography variant="h4" fontWeight="bold">
                PostureSense
              </Typography>
              <Typography variant="body1" color="text.secondary">
                Real-time pose detection and exercise tracking
              </Typography>
            </MDBox>
          </Grid>
          <Grid item xs={12}>
            <MDBox mb={3}>
              <FormControl fullWidth>
                <InputLabel>Select Exercise</InputLabel>
                <Select
                  value={selectedExercise}
                  label="Select Exercise"
                  onChange={(e) => {
                    setSelectedExercise(e.target.value);
                    counterRef.current = 0;
                    setDisplayCounter(0);
                    stageRef.current = null;
                    setDisplayStage(null);
                  }}
                >
                  {Object.entries(exercises).map(([key, exercise]) => (
                    <MenuItem key={key} value={key}>
                      {exercise.name}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
            </MDBox>
          </Grid>
          <Grid item xs={12}>
            <MDBox mb={3}>
              <Typography variant="h5" fontWeight="bold" color="primary">
                {exercises[selectedExercise].name}
              </Typography>
              <Typography variant="body1" color="text.secondary" sx={{ mt: 2 }}>
                Instructions:
              </Typography>
              {exercises[selectedExercise].instructions.map((instruction, index) => (
                <Typography key={index} variant="body2" color="text.secondary" sx={{ mt: 1 }}>
                  {index + 1}. {instruction}
                </Typography>
              ))}
              <Typography variant="body2" color="text.secondary" sx={{ mt: 2 }}>
                The tracker will count your reps when you:
              </Typography>
              <Typography variant="body2" color="text.secondary" sx={{ mt: 1 }}>
                â¢ Lower past {exercises[selectedExercise].downAngle}Â° (down position)
              </Typography>
              <Typography variant="body2" color="text.secondary">
                â¢ Move up past {exercises[selectedExercise].upAngle}Â° (up position)
              </Typography>
            </MDBox>
          </Grid>
          <Grid item xs={12}>
            <MDBox
              sx={{
                position: "relative",
                width: "100%",
                maxWidth: "800px",
                margin: "0 auto",
                backgroundColor: "#f9fafb",
                borderRadius: "12px",
                padding: "24px",
                border: "1px solid rgba(0,0,0,0.1)",
              }}
            >
              <video
                ref={videoRef}
                style={{
                  position: "absolute",
                  width: "100%",
                  height: "100%",
                  objectFit: "cover",
                  opacity: 0,
                  zIndex: 1,
                }}
                autoPlay
                playsInline
              />
              <canvas
                ref={canvasRef}
                style={{
                  width: "100%",
                  height: "500px",
                  borderRadius: "12px",
                  display: isCameraActive ? "block" : "none",
                  position: "relative",
                  zIndex: 2,
                }}
              />
              {!isCameraActive && (
                <MDBox
                  sx={{
                    display: "flex",
                    flexDirection: "column",
                    alignItems: "center",
                    justifyContent: "center",
                    height: "500px",
                  }}
                >
                  <Typography variant="body1" color="text.secondary" align="center">
                    Loading camera...
                  </Typography>
                  <MDButton
                    variant="contained"
                    color="info"
                    onClick={() => {
                      setIsCameraActive(true);
                      startCamera();
                    }}
                    sx={{ mt: 2 }}
                  >
                    Start Camera
                  </MDButton>
                </MDBox>
              )}
            </MDBox>
          </Grid>
        </Grid>
      </MDBox>
      <Footer />
    </DashboardLayout>
  );
}

export default PostureSense;
