/**
=========================================================
* Material Dashboard 2 React - v2.2.0
=========================================================
*/

// @mui material components
import Grid from "@mui/material/Grid";
import Typography from "@mui/material/Typography";
import { useEffect, useRef, useState } from "react";
import Card from "@mui/material/Card";
import Box from "@mui/material/Box";
import Select from "@mui/material/Select";
import MenuItem from "@mui/material/MenuItem";
import FormControl from "@mui/material/FormControl";
import InputLabel from "@mui/material/InputLabel";

// Material Dashboard 2 React components
import MDBox from "components/MDBox";
import MDButton from "components/MDButton";

// Material Dashboard 2 React example components
import DashboardLayout from "examples/LayoutContainers/DashboardLayout";
import DashboardNavbar from "examples/Navbars/DashboardNavbar";
import Footer from "examples/Footer";

// Exercise configurations
const exercises = {
  bicepCurls: {
    name: "Bicep Curls",
    instructions: [
      "Stand with your back straight and arms at your sides",
      "Keep your elbows close to your body",
      "Curl both arms up until your biceps are fully contracted",
      "Lower your arms back down with controlled movement"
    ],
    tracking: {
      leftArm: {
        shoulder: 11,
        elbow: 13,
        wrist: 15,
        downAngle: 160,
        upAngle: 30
      },
      rightArm: {
        shoulder: 12,
        elbow: 14,
        wrist: 16,
        downAngle: 160,
        upAngle: 30
      }
    }
  },
  squats: {
    name: "Squats",
    instructions: [
      "Stand with feet shoulder-width apart",
      "Keep your back straight",
      "Lower your body until thighs are parallel to ground",
      "Push back up to starting position"
    ],
    tracking: {
      leftLeg: {
        hip: 23,
        knee: 25,
        ankle: 27,
        downAngle: 160,
        upAngle: 30
      },
      rightLeg: {
        hip: 24,
        knee: 26,
        ankle: 28,
        downAngle: 160,
        upAngle: 30
      }
    }
  },
  shoulderPress: {
    name: "Shoulder Press",
    instructions: [
      "Stand with feet shoulder-width apart",
      "Hold weights at shoulder level",
      "Press weights overhead until arms are fully extended",
      "Lower weights back to shoulder level"
    ],
    tracking: {
      leftArm: {
        shoulder: 11,
        elbow: 13,
        wrist: 15,
        downAngle: 90,
        upAngle: 30
      },
      rightArm: {
        shoulder: 12,
        elbow: 14,
        wrist: 16,
        downAngle: 90,
        upAngle: 30
      }
    }
  }
};

function PostureSense() {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const stageRef = useRef(null);
  const counterRef = useRef(0);
  const [displayCounter, setDisplayCounter] = useState(0);
  const [displayStage, setDisplayStage] = useState(null);
  const [isCameraActive, setIsCameraActive] = useState(false);
  const [selectedExercise, setSelectedExercise] = useState("bicepCurls");
  const [leftStage, setLeftStage] = useState(null);
  const [rightStage, setRightStage] = useState(null);
  const [leftCounter, setLeftCounter] = useState(0);
  const [rightCounter, setRightCounter] = useState(0);
  const [leftAngle, setLeftAngle] = useState(0);
  const [rightAngle, setRightAngle] = useState(0);

  useEffect(() => {
    let video = null;
    let pose = null;
    let animationFrame = null;

    const startCamera = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ video: true });
        video = videoRef.current;
        video.srcObject = stream;

        // Wait for video to be ready
        await new Promise((resolve) => {
          video.onloadedmetadata = () => {
            video.play();
            resolve();
          };
        });

        // Initialize MediaPipe Pose
        const mp = await import("@mediapipe/pose");
        pose = new mp.Pose({
          locateFile: (file) => {
            return `https://cdn.jsdelivr.net/npm/@mediapipe/pose/${file}`;
          },
        });

        pose.setOptions({
          modelComplexity: 1,
          smoothLandmarks: true,
          minDetectionConfidence: 0.5,
          minTrackingConfidence: 0.5,
        });

        // Start processing
        const processFrame = async () => {
          if (video && pose && video.readyState === video.HAVE_ENOUGH_DATA) {
            await pose.send({ image: video });
            animationFrame = requestAnimationFrame(processFrame);
          }
        };

        processFrame();
        setIsCameraActive(true);
      } catch (error) {
        console.error("Error accessing camera:", error);
      }
    };

    startCamera();

    return () => {
      if (animationFrame) {
        cancelAnimationFrame(animationFrame);
      }
      if (video && video.srcObject) {
        video.srcObject.getTracks().forEach((track) => track.stop());
      }
      if (pose) {
        pose.close();
      }
      setIsCameraActive(false);
    };
  }, []); // Remove stage and counter from dependencies

  const calculateAngle = (landmarks, shoulder, elbow, wrist) => {
    const shoulderPoint = [
      landmarks[shoulder].x * canvasRef.current.width,
      landmarks[shoulder].y * canvasRef.current.height,
    ];
    const elbowPoint = [
      landmarks[elbow].x * canvasRef.current.width,
      landmarks[elbow].y * canvasRef.current.height,
    ];
    const wristPoint = [
      landmarks[wrist].x * canvasRef.current.width,
      landmarks[wrist].y * canvasRef.current.height,
    ];

    const angle = Math.atan2(wristPoint[1] - elbowPoint[1], wristPoint[0] - elbowPoint[0]) -
      Math.atan2(shoulderPoint[1] - elbowPoint[1], shoulderPoint[0] - elbowPoint[0]);

    let degrees = Math.abs((angle * 180.0) / Math.PI);
    if (degrees > 180.0) degrees = 360 - degrees;
    return degrees;
  };

  const detectPose = async () => {
    if (!videoRef.current || !canvasRef.current) return;

    const video = videoRef.current;
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    const exercise = exercises[selectedExercise];

    // Set canvas size to match video
    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;

    // Draw video frame
    ctx.save();
    ctx.scale(-1, 1);
    ctx.translate(-canvas.width, 0);
    ctx.drawImage(video, 0, 0, canvas.width, canvas.height);
    ctx.restore();

    // Detect pose
    const results = await pose.detectForVideo(video, Date.now());
    if (results.poseLandmarks) {
      const landmarks = results.poseLandmarks;

      // Track left side
      const leftConfig = exercise.tracking.leftArm || exercise.tracking.leftLeg;
      const leftAngle = calculateAngle(
        landmarks,
        leftConfig.shoulder || leftConfig.hip,
        leftConfig.elbow || leftConfig.knee,
        leftConfig.wrist || leftConfig.ankle
      );
      setLeftAngle(leftAngle);

      // Track right side
      const rightConfig = exercise.tracking.rightArm || exercise.tracking.rightLeg;
      const rightAngle = calculateAngle(
        landmarks,
        rightConfig.shoulder || rightConfig.hip,
        rightConfig.elbow || rightConfig.knee,
        rightConfig.wrist || rightConfig.ankle
      );
      setRightAngle(rightAngle);

      // Update counters based on exercise configuration
      if (leftAngle > leftConfig.downAngle) {
        setLeftStage("DOWN");
      }
      if (leftAngle < leftConfig.upAngle && leftStage === "DOWN") {
        setLeftStage("UP");
        setLeftCounter((prev) => prev + 1);
      }

      if (rightAngle > rightConfig.downAngle) {
        setRightStage("DOWN");
      }
      if (rightAngle < rightConfig.upAngle && rightStage === "DOWN") {
        setRightStage("UP");
        setRightCounter((prev) => prev + 1);
      }

      // Draw landmarks and angles
      ctx.save();
      ctx.scale(-1, 1);
      ctx.translate(-canvas.width, 0);

      // Draw left side landmarks
      ctx.beginPath();
      ctx.moveTo(
        landmarks[leftConfig.shoulder || leftConfig.hip].x * canvas.width,
        landmarks[leftConfig.shoulder || leftConfig.hip].y * canvas.height
      );
      ctx.lineTo(
        landmarks[leftConfig.elbow || leftConfig.knee].x * canvas.width,
        landmarks[leftConfig.elbow || leftConfig.knee].y * canvas.height
      );
      ctx.lineTo(
        landmarks[leftConfig.wrist || leftConfig.ankle].x * canvas.width,
        landmarks[leftConfig.wrist || leftConfig.ankle].y * canvas.height
      );
      ctx.strokeStyle = "#00ff00";
      ctx.lineWidth = 2;
      ctx.stroke();

      // Draw right side landmarks
      ctx.beginPath();
      ctx.moveTo(
        landmarks[rightConfig.shoulder || rightConfig.hip].x * canvas.width,
        landmarks[rightConfig.shoulder || rightConfig.hip].y * canvas.height
      );
      ctx.lineTo(
        landmarks[rightConfig.elbow || rightConfig.knee].x * canvas.width,
        landmarks[rightConfig.elbow || rightConfig.knee].y * canvas.height
      );
      ctx.lineTo(
        landmarks[rightConfig.wrist || rightConfig.ankle].x * canvas.width,
        landmarks[rightConfig.wrist || rightConfig.ankle].y * canvas.height
      );
      ctx.strokeStyle = "#ff0000";
      ctx.lineWidth = 2;
      ctx.stroke();

      // Draw angles
      ctx.font = "16px Arial";
      ctx.fillStyle = "#00ff00";
      ctx.fillText(`Left: ${Math.round(leftAngle)}°`, 20, 30);
      ctx.fillStyle = "#ff0000";
      ctx.fillText(`Right: ${Math.round(rightAngle)}°`, canvas.width - 100, 30);

      ctx.restore();

      // Draw counter and stage (normal orientation)
      ctx.fillStyle = "rgba(100, 100, 100, 0.8)";
      // Left side counter and stage
      ctx.fillRect(0, 0, 200, 70);
      ctx.fillStyle = "black";
      ctx.font = "16px Arial";
      ctx.fillText("LEFT REPS", 20, 20);
      ctx.fillStyle = "white";
      ctx.font = "24px Arial";
      ctx.fillText(leftCounter.toString(), 25, 60);
      ctx.fillStyle = "black";
      ctx.font = "16px Arial";
      ctx.fillText("STAGE", 100, 20);
      ctx.fillStyle = "white";
      ctx.font = "20px Arial";
      ctx.fillText(leftStage || "", 100, 60);

      // Right side counter and stage
      ctx.fillStyle = "rgba(100, 100, 100, 0.8)";
      ctx.fillRect(canvas.width - 200, 0, 200, 70);
      ctx.fillStyle = "black";
      ctx.font = "16px Arial";
      ctx.fillText("RIGHT REPS", canvas.width - 180, 20);
      ctx.fillStyle = "white";
      ctx.font = "24px Arial";
      ctx.fillText(rightCounter.toString(), canvas.width - 175, 60);
      ctx.fillStyle = "black";
      ctx.font = "16px Arial";
      ctx.fillText("STAGE", canvas.width - 100, 20);
      ctx.fillStyle = "white";
      ctx.font = "20px Arial";
      ctx.fillText(rightStage || "", canvas.width - 100, 60);
    }

    requestAnimationFrame(detectPose);
  };

  return (
    <DashboardLayout>
      <DashboardNavbar />
      <MDBox py={3}>
        <Grid container spacing={3}>
          <Grid item xs={12}>
            <MDBox mb={3}>
              <Typography variant="h4" fontWeight="bold">
                PostureSense
              </Typography>
              <Typography variant="body1" color="text.secondary">
                Real-time pose detection and exercise tracking
              </Typography>
            </MDBox>
          </Grid>
          <Grid item xs={12}>
            <MDBox mb={3}>
              <FormControl fullWidth>
                <InputLabel>Select Exercise</InputLabel>
                <Select
                  value={selectedExercise}
                  label="Select Exercise"
                  onChange={(e) => {
                    setSelectedExercise(e.target.value);
                    setLeftCounter(0);
                    setRightCounter(0);
                    setLeftStage(null);
                    setRightStage(null);
                  }}
                >
                  {Object.entries(exercises).map(([key, exercise]) => (
                    <MenuItem key={key} value={key}>
                      {exercise.name}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
            </MDBox>
          </Grid>
          <Grid item xs={12}>
            <MDBox mb={3}>
              <Typography variant="h5" fontWeight="bold" color="primary">
                {exercises[selectedExercise].name}
              </Typography>
              <Typography variant="body1" color="text.secondary" sx={{ mt: 2 }}>
                Instructions:
              </Typography>
              {exercises[selectedExercise].instructions.map((instruction, index) => (
                <Typography key={index} variant="body2" color="text.secondary" sx={{ mt: 1 }}>
                  {index + 1}. {instruction}
                </Typography>
              ))}
              <Typography variant="body2" color="text.secondary" sx={{ mt: 2 }}>
                The tracker will count your reps when you:
              </Typography>
              <Typography variant="body2" color="text.secondary" sx={{ mt: 1 }}>
                • Lower past {exercises[selectedExercise].tracking.leftArm?.downAngle || exercises[selectedExercise].tracking.leftLeg?.downAngle}° (down position)
              </Typography>
              <Typography variant="body2" color="text.secondary">
                • Move up past {exercises[selectedExercise].tracking.leftArm?.upAngle || exercises[selectedExercise].tracking.leftLeg?.upAngle}° (up position)
              </Typography>
            </MDBox>
          </Grid>
          <Grid item xs={12}>
            <MDBox
              sx={{
                position: "relative",
                width: "100%",
                maxWidth: "800px",
                margin: "0 auto",
                backgroundColor: "#f9fafb",
                borderRadius: "12px",
                padding: "24px",
                border: "1px solid rgba(0,0,0,0.1)",
              }}
            >
              <video
                ref={videoRef}
                style={{
                  position: "absolute",
                  width: "100%",
                  height: "100%",
                  objectFit: "cover",
                  opacity: 0,
                  zIndex: 1,
                }}
                autoPlay
                playsInline
              />
              <canvas
                ref={canvasRef}
                style={{
                  width: "100%",
                  height: "500px",
                  borderRadius: "12px",
                  display: isCameraActive ? "block" : "none",
                  position: "relative",
                  zIndex: 2,
                }}
              />
              {!isCameraActive && (
                <MDBox
                  sx={{
                    display: "flex",
                    flexDirection: "column",
                    alignItems: "center",
                    justifyContent: "center",
                    height: "500px",
                  }}
                >
                  <Typography variant="body1" color="text.secondary" align="center">
                    Loading camera...
                  </Typography>
                  <MDButton
                    variant="contained"
                    color="info"
                    onClick={() => {
                      setIsCameraActive(true);
                      startCamera();
                    }}
                    sx={{ mt: 2 }}
                  >
                    Start Camera
                  </MDButton>
                </MDBox>
              )}
            </MDBox>
          </Grid>
        </Grid>
      </MDBox>
      <Footer />
    </DashboardLayout>
  );
}

export default PostureSense;
